mapper.select:
select

from house a



		order by a.price asc


		order by a.price desc



	limit #{pageParams.offset},#{pageParams.limit}


	limit #{pageParams.limit}

select *
from community


		and id = #{id}


		and name = #{name}

select id,house_id,user_id,create_time,type
from house_user
where house_id = #{id} and user_id = #{userId} and type = #{type}

mapper.update:
update house
set rating = #{rating}
where id = #{id}

update house
set state = 2
where id = #{id}

mapper.delete:
delete from house_user
where house_id = #{id} and
user_id = #{userId} and type=#{type}
